import React, { useState, useEffect } from 'react';
import {
  Container,
  Typography,
  Box,
  List,
  ListItem,
  ListItemText,
  Paper,
  Divider,
  CircularProgress,
  TextField,
  InputAdornment
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import axios from 'axios';

interface Process {
  id: number;
  code: string;
  name: string;
  description: string;
}

interface AutomatedSystem {
  id: number;
  name: string;
  description: string;
  risk_description: string;
}

function App() {
  const [processes, setProcesses] = useState<Process[]>([]);
  const [filteredProcesses, setFilteredProcesses] = useState<Process[]>([]);
  const [selectedProcess, setSelectedProcess] = useState<Process | null>(null);
  const [systems, setSystems] = useState<AutomatedSystem[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    fetchProcesses();
  }, []);

  useEffect(() => {
    if (searchTerm) {
      // Если есть поисковый запрос, используем API поиска
      searchProcesses(searchTerm);
    } else {
      // Если поисковый запрос пустой, показываем все процессы
      setFilteredProcesses(processes);
    }
  }, [searchTerm, processes]);

  const fetchProcesses = async () => {
    try {
      const response = await axios.get('http://localhost:8000/processes');
      setProcesses(response.data);
      setFilteredProcesses(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching processes:', error);
      setLoading(false);
    }
  };

  const searchProcesses = async (query: string) => {
    try {
      const response = await axios.get(`http://localhost:8000/search?q=${encodeURIComponent(query)}`);
      setFilteredProcesses(response.data);
    } catch (error) {
      console.error('Error searching processes:', error);
    }
  };

  const fetchSystems = async (processId: number) => {
    try {
      const response = await axios.get(`http://localhost:8000/process/${processId}/systems`);
      setSystems(response.data);
    } catch (error) {
      console.error('Error fetching systems:', error);
    }
  };

  const handleProcessClick = async (process: Process) => {
    setSelectedProcess(process);
    await fetchSystems(process.id);
  };

  const handleSearchChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setSearchTerm(event.target.value);
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="100vh">
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Container maxWidth="lg">
      <Box my={4}>
        <Typography variant="h4" component="h1" gutterBottom align="center">
          Просмотр рисков по процессам
        </Typography>
        
        <Box mb={3}>
          <TextField
            fullWidth
            variant="outlined"
            placeholder="Поиск по названию или коду процесса..."
            value={searchTerm}
            onChange={handleSearchChange}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <SearchIcon />
                </InputAdornment>
              ),
            }}
          />
        </Box>
        
        <Box display="flex" gap={2}>
          <Paper elevation={3} sx={{ width: '30%', p: 2, maxHeight: '80vh', overflow: 'auto' }}>
            <Typography variant="h6" gutterBottom>
              Процессы
            </Typography>
            <List>
              {filteredProcesses.map((process) => (
                <ListItem
                  key={process.id}
                  button
                  onClick={() => handleProcessClick(process)}
                  selected={selectedProcess?.id === process.id}
                >
                  <ListItemText 
                    primary={process.name}
                    secondary={process.code ? `Код: ${process.code}` : undefined}
                  />
                </ListItem>
              ))}
              {filteredProcesses.length === 0 && (
                <Typography variant="body2" color="textSecondary" sx={{ p: 2 }}>
                  Процессы не найдены
                </Typography>
              )}
            </List>
          </Paper>

          <Paper elevation={3} sx={{ width: '70%', p: 2, maxHeight: '80vh', overflow: 'auto' }}>
            {selectedProcess ? (
              <>
                <Typography variant="h6" gutterBottom>
                  {selectedProcess.name}
                </Typography>
                {selectedProcess.code && (
                  <Typography variant="subtitle1" color="textSecondary" gutterBottom>
                    Код процесса: {selectedProcess.code}
                  </Typography>
                )}
                {selectedProcess.description && (
                  <Typography variant="body1" paragraph>
                    {selectedProcess.description}
                  </Typography>
                )}
                <Divider sx={{ my: 2 }} />
                <Typography variant="h6" gutterBottom>
                  Автоматизированные системы и риски
                </Typography>
                {systems.map((system) => (
                  <Box key={system.id} mb={3}>
                    <Typography variant="subtitle1" fontWeight="bold">
                      {system.name}
                    </Typography>
                    {system.description && (
                      <Typography variant="body2" paragraph>
                        {system.description}
                      </Typography>
                    )}
                    <Typography variant="body1" color="error">
                      Риск при отключении: {system.risk_description}
                    </Typography>
                    <Divider sx={{ mt: 2 }} />
                  </Box>
                ))}
              </>
            ) : (
              <Typography variant="body1" align="center">
                Выберите процесс слева для просмотра связанных рисков
              </Typography>
            )}
          </Paper>
        </Box>
      </Box>
    </Container>
  );
}

export default App;
