import React, { useState, useEffect } from 'react';
import {
  Container,
  TextField,
  List,
  ListItem,
  ListItemText,
  Typography,
  Paper,
  Box,
  Chip,
  Grid,
  Card,
  CardContent,
  Divider
} from '@mui/material';

interface Process {
  id: number;
  sid: string;
  name: string;
  risk_label: string;
  owner_block: string;
  department: string;
  integral_risk_level: string;
  highest_risk_level: string;
  rating: number;
}

interface Threat {
  id: number;
  type: string;
  scenario: string;
}

function App() {
  const [processes, setProcesses] = useState<Process[]>([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedProcess, setSelectedProcess] = useState<Process | null>(null);
  const [threats, setThreats] = useState<Threat[]>([]);

  useEffect(() => {
    if (searchTerm) {
      fetch(`http://localhost:8000/search?q=${encodeURIComponent(searchTerm)}`)
        .then(response => response.json())
        .then(data => setProcesses(data));
    } else {
      fetch('http://localhost:8000/processes')
        .then(response => response.json())
        .then(data => setProcesses(data));
    }
  }, [searchTerm]);

  useEffect(() => {
    if (selectedProcess) {
      fetch(`http://localhost:8000/process/${selectedProcess.id}/threats`)
        .then(response => response.json())
        .then(data => setThreats(data));
    }
  }, [selectedProcess]);

  const getRiskLevelColor = (level: string) => {
    const levelLower = level.toLowerCase();
    if (levelLower.includes('высок')) return '#ff4444';
    if (levelLower.includes('средн')) return '#ffbb33';
    if (levelLower.includes('низк')) return '#00C851';
    return '#33b5e5';
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4 }}>
      <Typography variant="h4" gutterBottom>
        Анализ рисков процессов
      </Typography>
      
      <TextField
        fullWidth
        label="Поиск по процессам"
        variant="outlined"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        sx={{ mb: 4 }}
      />

      <Grid container spacing={3}>
        <Grid item xs={12} md={4}>
          <Paper sx={{ maxHeight: 'calc(100vh - 200px)', overflow: 'auto' }}>
            <List>
              {processes.map((process) => (
                <ListItem
                  key={process.id}
                  button
                  selected={selectedProcess?.id === process.id}
                  onClick={() => setSelectedProcess(process)}
                >
                  <ListItemText
                    primary={process.name}
                    secondary={`SID: ${process.sid}`}
                  />
                </ListItem>
              ))}
            </List>
          </Paper>
        </Grid>

        <Grid item xs={12} md={8}>
          {selectedProcess && (
            <Card>
              <CardContent>
                <Typography variant="h5" gutterBottom>
                  {selectedProcess.name}
                </Typography>
                
                <Box sx={{ mb: 2 }}>
                  <Typography variant="subtitle1" color="text.secondary">
                    SID: {selectedProcess.sid}
                  </Typography>
                  <Typography variant="body2">
                    Блок-владелец: {selectedProcess.owner_block}
                  </Typography>
                  <Typography variant="body2">
                    Подразделение: {selectedProcess.department}
                  </Typography>
                </Box>

                <Box sx={{ mb: 2 }}>
                  <Chip
                    label={`Интегральный уровень риска: ${selectedProcess.integral_risk_level}`}
                    sx={{
                      bgcolor: getRiskLevelColor(selectedProcess.integral_risk_level),
                      color: 'white',
                      mb: 1
                    }}
                  />
                  <br />
                  <Chip
                    label={`Наивысший уровень риска: ${selectedProcess.highest_risk_level}`}
                    sx={{
                      bgcolor: getRiskLevelColor(selectedProcess.highest_risk_level),
                      color: 'white'
                    }}
                  />
                </Box>

                <Typography variant="h6" gutterBottom sx={{ mt: 3 }}>
                  Угрозы процесса:
                </Typography>
                
                {threats.map((threat) => (
                  <Paper key={threat.id} sx={{ p: 2, mb: 2 }}>
                    <Typography variant="subtitle1">
                      Тип угрозы: {threat.type}
                    </Typography>
                    <Typography variant="body2">
                      Сценарий: {threat.scenario}
                    </Typography>
                  </Paper>
                ))}
              </CardContent>
            </Card>
          )}
        </Grid>
      </Grid>
    </Container>
  );
}

export default App;
