import React, { useState, useEffect } from 'react';
import {
  Container,
  TextField,
  List,
  ListItem,
  ListItemText,
  Typography,
  Paper,
  Box,
  Chip,
  Grid,
  Card,
  CardContent,
  Modal,
  IconButton,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Tabs,
  Tab,
  Button
} from '@mui/material';
import CloseIcon from '@mui/icons-material/Close';

interface Process {
  id: number;
  sid: string;
  name: string;
  risk_label: string;
  owner_block: string;
  department: string;
  rating: number;
}

interface Threat {
  id: number;
  type: string;
  scenario: string;
  integral_risk_level: string;
  highest_risk_level: string;
}

interface RiskDetail {
  id: number;
  process_sid: string;
  threat_type: string;
  threat_scenario: string;
  impact_type: string;
  risk_impact: string;
  risk_assessment: string;
  risk_label: string;
  risk_assessment_explanation: string;
}

interface DetailedRiskReport {
  id: number;
  process: string;
  threat_type: string;
  threat_scenario: string;
  risk_category: string;
  risk_subcategory: string;
  risk_group: string;
  risk_subgroup: string;
  integral_risk: string;
  operational_risk: string;
  reputational_risk: string;
  regulatory_risk: string;
  financial_risk: string;
  impact_assessment: string;
  probability_assessment: string;
  control_assessment: string;
  risk_level: string;
}

function App() {
  const [processes, setProcesses] = useState<Process[]>([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedProcess, setSelectedProcess] = useState<Process | null>(null);
  const [threats, setThreats] = useState<Threat[]>([]);
  const [selectedThreat, setSelectedThreat] = useState<Threat | null>(null);
  const [riskDetails, setRiskDetails] = useState<RiskDetail[]>([]);
  const [detailedRisks, setDetailedRisks] = useState<DetailedRiskReport[]>([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [activeTab, setActiveTab] = useState(0);

  useEffect(() => {
    if (searchTerm) {
      fetch(`http://localhost:8000/search?q=${encodeURIComponent(searchTerm)}`)
        .then(response => response.json())
        .then(data => setProcesses(data));
    } else {
      fetch('http://localhost:8000/processes')
        .then(response => response.json())
        .then(data => setProcesses(data));
    }
  }, [searchTerm]);

  useEffect(() => {
    if (selectedProcess) {
      fetch(`http://localhost:8000/process/${selectedProcess.id}/threats`)
        .then(response => response.json())
        .then(data => setThreats(data));
    }
  }, [selectedProcess]);

  useEffect(() => {
    if (selectedThreat) {
      console.log('Загрузка деталей для угрозы:', selectedThreat);
      // Загружаем базовые детали риска
      fetch(`http://localhost:8000/threat/${selectedThreat.id}/details`)
        .then(response => {
          if (!response.ok) {
            throw new Error('Risk details not found');
          }
          return response.json();
        })
        .then(data => {
          console.log('Получены базовые детали:', data);
          setRiskDetails(data || []);
        })
        .catch(error => {
          console.error('Error fetching risk details:', error);
          setRiskDetails([]);
        });

      // Загружаем расширенные детали риска
      fetch(`http://localhost:8000/threat/${selectedThreat.id}/detailed-risks`)
        .then(response => {
          if (!response.ok) {
            throw new Error('Detailed risks not found');
          }
          return response.json();
        })
        .then(data => {
          console.log('Получены детальные риски:', data);
          setDetailedRisks(data || []);
        })
        .catch(error => {
          console.error('Error fetching detailed risks:', error);
          setDetailedRisks([]);
        });
    }
  }, [selectedThreat]);

  const handleThreatClick = (threat: Threat) => {
    console.log('Клик по угрозе:', threat);
    setSelectedThreat(threat);
    setIsModalOpen(true); // Добавляем явное открытие модального окна
  };

  const handleCloseModal = () => {
    console.log('Закрытие модального окна');
    setIsModalOpen(false);
    setSelectedThreat(null);
    setActiveTab(0);
  };

  const handleTabChange = (event: React.SyntheticEvent, newValue: number) => {
    setActiveTab(newValue);
  };

  const getRiskLevelColor = (level: string) => {
    const levelLower = level.toLowerCase();
    if (levelLower.includes('высок')) return '#ff4444';
    if (levelLower.includes('средн')) return '#ffbb33';
    if (levelLower.includes('низк')) return '#00C851';
    return '#33b5e5';
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4 }}>
      <Typography variant="h4" gutterBottom>
        Анализ рисков процессов
      </Typography>
      
      <TextField
        fullWidth
        label="Поиск по процессам"
        variant="outlined"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        sx={{ mb: 4 }}
      />

      <Grid container spacing={3}>
        <Grid item xs={12} md={4}>
          <Paper sx={{ maxHeight: 'calc(100vh - 200px)', overflow: 'auto' }}>
            <List>
              {processes.map((process) => (
                <ListItem
                  key={process.id}
                  button
                  selected={selectedProcess?.id === process.id}
                  onClick={() => setSelectedProcess(process)}
                >
                  <ListItemText
                    primary={process.name}
                    secondary={`SID: ${process.sid}`}
                  />
                </ListItem>
              ))}
            </List>
          </Paper>
        </Grid>

        <Grid item xs={12} md={8}>
          {selectedProcess && (
            <Card>
              <CardContent>
                <Typography variant="h5" gutterBottom>
                  {selectedProcess.name}
                </Typography>
                
                <Box sx={{ mb: 2 }}>
                  <Typography variant="subtitle1" color="text.secondary">
                    SID: {selectedProcess.sid}
                  </Typography>
                  <Typography variant="body2">
                    Блок-владелец: {selectedProcess.owner_block}
                  </Typography>
                  <Typography variant="body2">
                    Подразделение: {selectedProcess.department}
                  </Typography>
                </Box>

                <Typography variant="h6" gutterBottom sx={{ mt: 3 }}>
                  Угрозы процесса:
                </Typography>
                
                {threats.map((threat) => (
                  <Button
                    key={threat.id}
                    onClick={() => handleThreatClick(threat)}
                    fullWidth
                    sx={{
                      mb: 2,
                      textAlign: 'left',
                      display: 'block'
                    }}
                  >
                    <Paper 
                      sx={{ 
                        p: 2,
                        width: '100%'
                      }}
                      elevation={2}
                    >
                      <Typography variant="subtitle1">
                        Тип угрозы: {threat.type}
                      </Typography>
                      <Typography variant="body2" sx={{ mb: 1 }}>
                        Сценарий: {threat.scenario}
                      </Typography>
                      <Box sx={{ mt: 2 }}>
                        <Chip
                          label={`Интегральный уровень риска: ${threat.integral_risk_level}`}
                          sx={{
                            bgcolor: getRiskLevelColor(threat.integral_risk_level),
                            color: 'white',
                            mb: 1,
                            mr: 1
                          }}
                        />
                        <Chip
                          label={`Наивысший уровень риска: ${threat.highest_risk_level}`}
                          sx={{
                            bgcolor: getRiskLevelColor(threat.highest_risk_level),
                            color: 'white'
                          }}
                        />
                      </Box>
                    </Paper>
                  </Button>
                ))}
              </CardContent>
            </Card>
          )}
        </Grid>
      </Grid>

      <Modal
        open={isModalOpen}
        onClose={handleCloseModal}
        aria-labelledby="modal-title"
        aria-describedby="modal-description"
      >
        <Box sx={{
          position: 'absolute',
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          width: '90%',
          maxWidth: 1000,
          bgcolor: 'background.paper',
          boxShadow: 24,
          p: 4,
          maxHeight: '90vh',
          overflow: 'auto'
        }}>
          {selectedThreat && (
            <>
              <IconButton
                aria-label="close"
                onClick={handleCloseModal}
                sx={{
                  position: 'absolute',
                  right: 8,
                  top: 8
                }}
              >
                <CloseIcon />
              </IconButton>

              <Typography variant="h6" gutterBottom>
                {selectedThreat.type}
              </Typography>
              <Typography variant="body1" gutterBottom>
                {selectedThreat.scenario}
              </Typography>

              <Box sx={{ borderBottom: 1, borderColor: 'divider', mb: 2 }}>
                <Tabs value={activeTab} onChange={handleTabChange}>
                  <Tab label="Базовая информация" />
                  <Tab label="Детальный отчет" />
                </Tabs>
              </Box>

              {activeTab === 0 && (
                <>
                  <Typography variant="h6" gutterBottom>
                    Детали риска
                  </Typography>
                  {Array.isArray(riskDetails) && riskDetails.length > 0 ? (
                    <TableContainer>
                      <Table>
                        <TableHead>
                          <TableRow>
                            <TableCell>Тип влияния</TableCell>
                            <TableCell>Воздействие риска</TableCell>
                            <TableCell>Оценка риска</TableCell>
                            <TableCell>Метка риска</TableCell>
                          </TableRow>
                        </TableHead>
                        <TableBody>
                          {riskDetails.map((detail) => (
                            <TableRow key={detail.id}>
                              <TableCell>{detail.impact_type}</TableCell>
                              <TableCell>{detail.risk_impact}</TableCell>
                              <TableCell>{detail.risk_assessment}</TableCell>
                              <TableCell>
                                <Chip
                                  label={detail.risk_label}
                                  sx={{
                                    bgcolor: getRiskLevelColor(detail.risk_label),
                                    color: 'white'
                                  }}
                                />
                              </TableCell>
                            </TableRow>
                          ))}
                        </TableBody>
                      </Table>
                    </TableContainer>
                  ) : (
                    <Typography>Нет доступных деталей риска</Typography>
                  )}
                </>
              )}

              {activeTab === 1 && (
                <>
                  <Typography variant="h6" gutterBottom>
                    Детальный отчет по процессу
                  </Typography>
                  <Box sx={{ mb: 3 }}>
                    <Typography variant="subtitle1" color="text.secondary">
                      Процесс: {selectedProcess?.name}
                    </Typography>
                    <Typography variant="body2">
                      SID: {selectedProcess?.sid}
                    </Typography>
                    <Typography variant="body2">
                      Блок-владелец: {selectedProcess?.owner_block}
                    </Typography>
                  </Box>
                  {Array.isArray(detailedRisks) && detailedRisks.length > 0 ? (
                    <TableContainer>
                      <Table>
                        <TableHead>
                          <TableRow>
                            <TableCell>Категория риска</TableCell>
                            <TableCell>Подкатегория</TableCell>
                            <TableCell>Группа</TableCell>
                            <TableCell>Интегральный риск</TableCell>
                            <TableCell>Уровень риска</TableCell>
                          </TableRow>
                        </TableHead>
                        <TableBody>
                          {detailedRisks.map((risk) => (
                            <TableRow key={risk.id}>
                              <TableCell>{risk.risk_category}</TableCell>
                              <TableCell>{risk.risk_subcategory}</TableCell>
                              <TableCell>{risk.risk_group}</TableCell>
                              <TableCell>{risk.integral_risk}</TableCell>
                              <TableCell>
                                <Chip
                                  label={risk.risk_level}
                                  sx={{
                                    bgcolor: getRiskLevelColor(risk.risk_level),
                                    color: 'white'
                                  }}
                                />
                              </TableCell>
                            </TableRow>
                          ))}
                        </TableBody>
                      </Table>
                    </TableContainer>
                  ) : (
                    <Typography>Нет доступных детальных данных для этого процесса</Typography>
                  )}
                </>
              )}
            </>
          )}
        </Box>
      </Modal>
    </Container>
  );
}

export default App;
