import React, { useState, useEffect } from 'react';
import {
  Container,
  TextField,
  List,
  ListItem,
  ListItemText,
  Typography,
  Paper,
  Box,
  Chip,
  Grid,
  Card,
  CardContent,
  Modal,
  IconButton,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Tabs,
  Tab
} from '@mui/material';
import CloseIcon from '@mui/icons-material/Close';

interface Process {
  id: number;
  sid: string;
  name: string;
  risk_label: string;
  owner_block: string;
  department: string;
  rating: number;
}

interface Threat {
  id: number;
  type: string;
  scenario: string;
  integral_risk_level: string;
  highest_risk_level: string;
}

interface RiskDetail {
  id: number;
  process_sid: string;
  threat_type: string;
  threat_scenario: string;
  impact_type: string;
  risk_impact: string;
  risk_assessment: string;
  risk_label: string;
  risk_assessment_explanation: string;
}

interface DetailedRiskReport {
  id: number;
  process: string;
  threat_type: string;
  threat_scenario: string;
  risk_category: string;
  risk_subcategory: string;
  risk_group: string;
  risk_subgroup: string;
  integral_risk: string;
  operational_risk: string;
  reputational_risk: string;
  regulatory_risk: string;
  financial_risk: string;
  impact_assessment: string;
  probability_assessment: string;
  control_assessment: string;
  risk_level: string;
}

function App() {
  const [processes, setProcesses] = useState<Process[]>([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedProcess, setSelectedProcess] = useState<Process | null>(null);
  const [threats, setThreats] = useState<Threat[]>([]);
  const [selectedThreat, setSelectedThreat] = useState<Threat | null>(null);
  const [riskDetails, setRiskDetails] = useState<RiskDetail[]>([]);
  const [detailedRisks, setDetailedRisks] = useState<DetailedRiskReport[]>([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [activeTab, setActiveTab] = useState(0);

  useEffect(() => {
    if (searchTerm) {
      fetch(`http://localhost:8000/search?q=${encodeURIComponent(searchTerm)}`)
        .then(response => response.json())
        .then(data => setProcesses(data));
    } else {
      fetch('http://localhost:8000/processes')
        .then(response => response.json())
        .then(data => setProcesses(data));
    }
  }, [searchTerm]);

  useEffect(() => {
    if (selectedProcess) {
      fetch(`http://localhost:8000/process/${selectedProcess.id}/threats`)
        .then(response => response.json())
        .then(data => setThreats(data));
    }
  }, [selectedProcess]);

  useEffect(() => {
    if (selectedThreat) {
      // Загружаем базовые детали риска
      fetch(`http://localhost:8000/threat/${selectedThreat.id}/details`)
        .then(response => response.json())
        .then(data => {
          setRiskDetails(data);
          setIsModalOpen(true);
        })
        .catch(error => {
          console.error('Error fetching risk details:', error);
        });

      // Загружаем расширенные детали риска
      fetch(`http://localhost:8000/threat/${selectedThreat.id}/detailed-risks`)
        .then(response => response.json())
        .then(data => {
          setDetailedRisks(data);
        })
        .catch(error => {
          console.error('Error fetching detailed risks:', error);
        });
    }
  }, [selectedThreat]);

  const handleThreatClick = (threat: Threat) => {
    setSelectedThreat(threat);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setSelectedThreat(null);
    setActiveTab(0);
  };

  const handleTabChange = (event: React.SyntheticEvent, newValue: number) => {
    setActiveTab(newValue);
  };

  const getRiskLevelColor = (level: string) => {
    const levelLower = level.toLowerCase();
    if (levelLower.includes('высок')) return '#ff4444';
    if (levelLower.includes('средн')) return '#ffbb33';
    if (levelLower.includes('низк')) return '#00C851';
    return '#33b5e5';
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4 }}>
      <Typography variant="h4" gutterBottom>
        Анализ рисков процессов
      </Typography>
      
      <TextField
        fullWidth
        label="Поиск по процессам"
        variant="outlined"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        sx={{ mb: 4 }}
      />

      <Grid container spacing={3}>
        <Grid item xs={12} md={4}>
          <Paper sx={{ maxHeight: 'calc(100vh - 200px)', overflow: 'auto' }}>
            <List>
              {processes.map((process) => (
                <ListItem
                  key={process.id}
                  button
                  selected={selectedProcess?.id === process.id}
                  onClick={() => setSelectedProcess(process)}
                >
                  <ListItemText
                    primary={process.name}
                    secondary={`SID: ${process.sid}`}
                  />
                </ListItem>
              ))}
            </List>
          </Paper>
        </Grid>

        <Grid item xs={12} md={8}>
          {selectedProcess && (
            <Card>
              <CardContent>
                <Typography variant="h5" gutterBottom>
                  {selectedProcess.name}
                </Typography>
                
                <Box sx={{ mb: 2 }}>
                  <Typography variant="subtitle1" color="text.secondary">
                    SID: {selectedProcess.sid}
                  </Typography>
                  <Typography variant="body2">
                    Блок-владелец: {selectedProcess.owner_block}
                  </Typography>
                  <Typography variant="body2">
                    Подразделение: {selectedProcess.department}
                  </Typography>
                </Box>

                <Typography variant="h6" gutterBottom sx={{ mt: 3 }}>
                  Угрозы процесса:
                </Typography>
                
                {threats.map((threat) => (
                  <Paper 
                    key={threat.id} 
                    sx={{ 
                      p: 2, 
                      mb: 2,
                      cursor: 'pointer',
                      '&:hover': {
                        backgroundColor: 'rgba(0, 0, 0, 0.04)'
                      }
                    }}
                    onClick={() => handleThreatClick(threat)}
                  >
                    <Typography variant="subtitle1">
                      Тип угрозы: {threat.type}
                    </Typography>
                    <Typography variant="body2" sx={{ mb: 1 }}>
                      Сценарий: {threat.scenario}
                    </Typography>
                    <Box sx={{ mt: 2 }}>
                      <Chip
                        label={`Интегральный уровень риска: ${threat.integral_risk_level}`}
                        sx={{
                          bgcolor: getRiskLevelColor(threat.integral_risk_level),
                          color: 'white',
                          mb: 1,
                          mr: 1
                        }}
                      />
                      <Chip
                        label={`Наивысший уровень риска: ${threat.highest_risk_level}`}
                        sx={{
                          bgcolor: getRiskLevelColor(threat.highest_risk_level),
                          color: 'white'
                        }}
                      />
                    </Box>
                  </Paper>
                ))}
              </CardContent>
            </Card>
          )}
        </Grid>
      </Grid>

      <Modal
        open={isModalOpen}
        onClose={handleCloseModal}
        aria-labelledby="risk-details-modal"
        sx={{
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center'
        }}
      >
        <Paper sx={{ 
          width: '90%', 
          maxWidth: 800, 
          maxHeight: '90vh',
          overflow: 'auto',
          p: 3,
          position: 'relative'
        }}>
          <IconButton
            sx={{
              position: 'absolute',
              right: 8,
              top: 8
            }}
            onClick={handleCloseModal}
          >
            <CloseIcon />
          </IconButton>

          {selectedThreat && (
            <>
              <Typography variant="h6" gutterBottom>
                Детали угрозы
              </Typography>

              <Box sx={{ borderBottom: 1, borderColor: 'divider', mb: 2 }}>
                <Tabs value={activeTab} onChange={handleTabChange}>
                  <Tab label="Основная информация" />
                  <Tab label="Расширенный анализ" />
                </Tabs>
              </Box>

              {activeTab === 0 && (
                <>
                  <Typography variant="subtitle1" gutterBottom>
                    {selectedThreat.type}
                  </Typography>
                  <Typography variant="body1" gutterBottom>
                    {selectedThreat.scenario}
                  </Typography>

                  <TableContainer component={Paper} sx={{ mt: 2 }}>
                    <Table>
                      <TableHead>
                        <TableRow>
                          <TableCell>Тип влияния</TableCell>
                          <TableCell>Воздействие риска</TableCell>
                          <TableCell>Оценка риска</TableCell>
                          <TableCell>Метка риска</TableCell>
                        </TableRow>
                      </TableHead>
                      <TableBody>
                        {riskDetails.map((detail) => (
                          <TableRow key={detail.id}>
                            <TableCell>{detail.impact_type}</TableCell>
                            <TableCell>{detail.risk_impact}</TableCell>
                            <TableCell>{detail.risk_assessment}</TableCell>
                            <TableCell>
                              <Chip
                                label={detail.risk_label}
                                sx={{
                                  bgcolor: getRiskLevelColor(detail.risk_label),
                                  color: 'white'
                                }}
                              />
                            </TableCell>
                          </TableRow>
                        ))}
                      </TableBody>
                    </Table>
                  </TableContainer>

                  {riskDetails.length > 0 && (
                    <Box sx={{ mt: 2 }}>
                      <Typography variant="subtitle2" gutterBottom>
                        Автопояснение результатов оценки риска:
                      </Typography>
                      <Typography variant="body2">
                        {riskDetails[0].risk_assessment_explanation}
                      </Typography>
                    </Box>
                  )}
                </>
              )}

              {activeTab === 1 && detailedRisks.length > 0 && (
                <>
                  <Grid container spacing={2}>
                    <Grid item xs={12} md={6}>
                      <Card>
                        <CardContent>
                          <Typography variant="h6" gutterBottom>
                            Категоризация риска
                          </Typography>
                          <Typography variant="body2">
                            <strong>Категория:</strong> {detailedRisks[0].risk_category}
                          </Typography>
                          <Typography variant="body2">
                            <strong>Подкатегория:</strong> {detailedRisks[0].risk_subcategory}
                          </Typography>
                          <Typography variant="body2">
                            <strong>Группа:</strong> {detailedRisks[0].risk_group}
                          </Typography>
                          <Typography variant="body2">
                            <strong>Подгруппа:</strong> {detailedRisks[0].risk_subgroup}
                          </Typography>
                        </CardContent>
                      </Card>
                    </Grid>

                    <Grid item xs={12} md={6}>
                      <Card>
                        <CardContent>
                          <Typography variant="h6" gutterBottom>
                            Оценки рисков
                          </Typography>
                          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 1 }}>
                            {[
                              { label: 'Интегральный риск', value: detailedRisks[0].integral_risk },
                              { label: 'Операционный риск', value: detailedRisks[0].operational_risk },
                              { label: 'Репутационный риск', value: detailedRisks[0].reputational_risk },
                              { label: 'Регуляторный риск', value: detailedRisks[0].regulatory_risk },
                              { label: 'Финансовый риск', value: detailedRisks[0].financial_risk }
                            ].map(({ label, value }) => (
                              <Chip
                                key={label}
                                label={`${label}: ${value}`}
                                sx={{
                                  bgcolor: getRiskLevelColor(value),
                                  color: 'white'
                                }}
                              />
                            ))}
                          </Box>
                        </CardContent>
                      </Card>
                    </Grid>

                    <Grid item xs={12}>
                      <Card>
                        <CardContent>
                          <Typography variant="h6" gutterBottom>
                            Параметры оценки
                          </Typography>
                          <TableContainer>
                            <Table>
                              <TableBody>
                                <TableRow>
                                  <TableCell><strong>Оценка воздействия:</strong></TableCell>
                                  <TableCell>{detailedRisks[0].impact_assessment}</TableCell>
                                </TableRow>
                                <TableRow>
                                  <TableCell><strong>Оценка вероятности:</strong></TableCell>
                                  <TableCell>{detailedRisks[0].probability_assessment}</TableCell>
                                </TableRow>
                                <TableRow>
                                  <TableCell><strong>Оценка контроля:</strong></TableCell>
                                  <TableCell>{detailedRisks[0].control_assessment}</TableCell>
                                </TableRow>
                                <TableRow>
                                  <TableCell><strong>Уровень риска:</strong></TableCell>
                                  <TableCell>
                                    <Chip
                                      label={detailedRisks[0].risk_level}
                                      sx={{
                                        bgcolor: getRiskLevelColor(detailedRisks[0].risk_level),
                                        color: 'white'
                                      }}
                                    />
                                  </TableCell>
                                </TableRow>
                              </TableBody>
                            </Table>
                          </TableContainer>
                        </CardContent>
                      </Card>
                    </Grid>
                  </Grid>
                </>
              )}
            </>
          )}
        </Paper>
      </Modal>
    </Container>
  );
}

export default App;
