import React, { useState, useEffect, useMemo } from 'react';
import './index.css';

// Определяем интерфейсы для наших данных
interface Process {
  id: number;
  sid: string;
  name: string;
  risk_label: string;
  owner_block: string;
  department: string;
  rating: number;
}

interface Threat {
  id: number;
  type: string;
  scenario: string;
  integral_risk_level: string;
  highest_risk_level: string;
  process_sid: string;
  threat_rating: string;
  threat_rating_color: string;
}

interface RiskDetail {
  id: number;
  as_reserved_in_rcod: string;
  risk_label: string;
  rto_hours: string;
  mtpd: string;
  tr: string;
  high_risk_count?: number | string;
  total_risk_count?: number | string;
  process_threat_rating?: string;
}

interface DetailedRiskReport {
  id: number;
  process: string;
  process_sid: string;
  threat_type: string;
  threat_scenario: string;
  impact_type: string;
  risk_subcategory: string;
  risk_group: string;
  risk_subgroup: string;
  integral_risk: string;
  operational_risk: string;
  reputational_risk: string;
  regulatory_risk: string;
  financial_risk: string;
  impact_assessment: string;
  probability_assessment: string;
  control_assessment: string;
  risk_level: string;
  rto_hours: string;
  mtpd: string;
  tr: string;
  risk_assessment_explanation: string;
  as_reserved_in_rcod: string;
}

interface FilterState {
  impact_type: string[];
  rto_hours: string[];
  mtpd: string[];
  tr: string[];
  risk_assessment_explanation: string[];
}

// Функция для форматирования значений
const formatValue = (value: string | null | undefined) => {
  console.log('Formatting value:', value);
  if (value === null || value === undefined || value === '') {
    console.log('Value is empty, returning "Нет данных"');
    return 'Нет данных';
  }
  console.log('Returning formatted value:', value);
  return value;
};

// Компонент поиска процессов
const ProcessSearch: React.FC<{ onSearch: (query: string) => void }> = ({ onSearch }) => {
  return (
    <div className="search-container">
      <input
        type="text"
        placeholder="Поиск по названию или коду процесса..."
        onChange={(e) => onSearch(e.target.value)}
        className="search-input"
      />
    </div>
  );
};

// Компонент визуализации временных метрик
const TimeMetricsVisualizer: React.FC<{
  rto: number;
  mtpd: number;
  tr: number;
}> = ({ rto, mtpd, tr }) => {
  console.log('TimeMetricsVisualizer received:', { rto, mtpd, tr });
  
  // Преобразуем значения в минуты для лучшей визуализации
  const rtoMinutes = rto * 60;
  const mtpdMinutes = mtpd * 60;
  const trMinutes = tr * 60;
  
  const criticalMtpdMinutes = mtpdMinutes * 1.3;
  const maxValue = Math.max(criticalMtpdMinutes, trMinutes) * 1.1; // +10% для отступа

  // Преобразуем значения в проценты для отображения
  const rtoPercent = (rtoMinutes / maxValue) * 100;
  const mtpdPercent = (mtpdMinutes / maxValue) * 100;
  const criticalMtpdPercent = (criticalMtpdMinutes / maxValue) * 100;
  const trPercent = (trMinutes / maxValue) * 100;

  console.log('Calculated percentages:', {
    rtoPercent,
    mtpdPercent,
    criticalMtpdPercent,
    trPercent
  });

  return (
    <div className="time-metrics-container">
      <div className="time-metrics-bar">
        <div className="time-metrics-scale">
          {[...Array(11)].map((_, i) => {
            const valueMinutes = (maxValue * i / 10);
            const valueHours = (valueMinutes / 60).toFixed(1);
            const percent = (i * 10);
            return (
              <div
                key={i}
                className="scale-mark"
                style={{ left: `${percent}%` }}
              >
                <div className="scale-line"></div>
                <div className="scale-value">{valueHours}ч</div>
              </div>
            );
          })}
        </div>
        <div className="metrics-indicators">
          <div
            className="rto-indicator"
            style={{ left: `${rtoPercent}%` }}
            title={`RTO: ${rto.toFixed(1)}ч`}
          >
            <div className="indicator-line"></div>
            <div className="indicator-label">RTO</div>
          </div>
          <div
            className="mtpd-indicator"
            style={{ left: `${mtpdPercent}%` }}
            title={`MTPD: ${mtpd.toFixed(1)}ч`}
          >
            <div className="indicator-line"></div>
            <div className="indicator-label">MTPD</div>
          </div>
          <div
            className="critical-mtpd-indicator"
            style={{ left: `${criticalMtpdPercent}%` }}
            title={`Critical MTPD: ${(mtpd * 1.3).toFixed(1)}ч`}
          >
            <div className="indicator-line"></div>
            <div className="indicator-label">Critical MTPD</div>
          </div>
          <div
            className="tr-indicator"
            style={{ left: `${trPercent}%` }}
            title={`TR: ${tr.toFixed(1)}ч`}
          >
            <div className="indicator-line"></div>
            <div className="indicator-label">TR</div>
          </div>
        </div>
      </div>
    </div>
  );
};

// Компонент мультиселект фильтра
const MultiSelectFilter: React.FC<{
  column: keyof FilterState;
  values: string[];
  selectedValues: string[];
  onChange: (column: keyof FilterState, values: string[]) => void;
}> = ({ column, values, selectedValues, onChange }) => {
  const [isOpen, setIsOpen] = useState(false);

  const getDisplayName = (column: string) => {
    const names: { [key: string]: string } = {
      impact_type: 'Тип влияния',
      rto_hours: 'RTO процесса',
      mtpd: 'MTPD процесса',
      tr: 'TR',
      risk_assessment_explanation: 'Автопояснение'
    };
    return names[column] || column;
  };

  const toggleValue = (value: string) => {
    const newValues = selectedValues.includes(value)
      ? selectedValues.filter(v => v !== value)
      : [...selectedValues, value];
    onChange(column, newValues);
  };

  return (
    <div className="multiselect-filter">
      <div 
        className="multiselect-header" 
        onClick={() => setIsOpen(!isOpen)}
      >
        <span>{getDisplayName(column)}</span>
        <span className="selected-count">
          {selectedValues.length ? `Выбрано: ${selectedValues.length}` : 'Все'}
        </span>
      </div>
      {isOpen && (
        <div className="multiselect-options">
          {values.map(value => (
            <label key={value} className="option-label">
              <input
                type="checkbox"
                checked={selectedValues.includes(value)}
                onChange={() => toggleValue(value)}
              />
              <span>{formatValue(value)}</span>
            </label>
          ))}
        </div>
      )}
    </div>
  );
};

// Компонент для отображения базовой информации о риске
const RiskDetails: React.FC<{ riskDetails: RiskDetail | null }> = ({ riskDetails }) => {
  console.log('RiskDetails component received:', riskDetails);

  if (!riskDetails) {
    console.log('No risk details data available');
    return <div>Нет данных для отображения.</div>;
  }

  // Предполагаем, что riskDetails содержит новые поля для базовой информации
  // Если их нет, показываем "Нет данных"
  const highRiskCount = (riskDetails.high_risk_count !== undefined) ? riskDetails.high_risk_count : 'Нет данных';
  const totalRiskCount = (riskDetails.total_risk_count !== undefined) ? riskDetails.total_risk_count : 'Нет данных';
  const processThreatRating = riskDetails.process_threat_rating || 'Нет данных';

  console.log('Processed risk details:', {
    highRiskCount,
    totalRiskCount,
    processThreatRating
  });

  return (
    <div className="risk-details">
      <h3>Базовая информация</h3>
      <table className="details-table">
        <tbody>
          <tr>
            <td>АС зарезервирована в РЦОД:</td>
            <td>{formatValue(riskDetails.as_reserved_in_rcod)}</td>
          </tr>
          <tr>
            <td>Количество высоких рисков (числитель метки):</td>
            <td>{highRiskCount}</td>
          </tr>
          <tr>
            <td>Количество рисков (знаменатель метки):</td>
            <td>{totalRiskCount}</td>
          </tr>
          <tr>
            <td>Рейтинг процесса для угрозы = по максимальным рискам =:</td>
            <td>{processThreatRating}</td>
          </tr>
        </tbody>
      </table>
    </div>
  );
};

// Компонент для детального отчета
const DetailedRisks: React.FC<{ detailedRisks: DetailedRiskReport[] }> = ({ detailedRisks }) => {
  console.log('DetailedRisks component received:', detailedRisks);

  const [filters, setFilters] = useState<FilterState>({
    impact_type: [],
    rto_hours: [],
    mtpd: [],
    tr: [],
    risk_assessment_explanation: []
  });

  // Получаем уникальные значения для каждой колонки
  const uniqueValues = useMemo(() => {
    console.log('Calculating unique values for:', detailedRisks);
    const values: { [key in keyof FilterState]: Set<string> } = {
      impact_type: new Set(),
      rto_hours: new Set(),
      mtpd: new Set(),
      tr: new Set(),
      risk_assessment_explanation: new Set()
    };

    detailedRisks.forEach(risk => {
      console.log('Processing risk:', risk);
      Object.keys(values).forEach(key => {
        const value = risk[key as keyof DetailedRiskReport];
        if (value) {
          console.log(`Adding value for ${key}:`, value);
          values[key as keyof FilterState].add(value.toString());
        }
      });
    });

    const result = Object.fromEntries(
      Object.entries(values).map(([key, set]) => [key, Array.from(set).sort()])
    ) as { [key in keyof FilterState]: string[] };
    console.log('Unique values calculated:', result);
    return result;
  }, [detailedRisks]);

  const handleFilterChange = (column: keyof FilterState, values: string[]) => {
    console.log('Filter change:', { column, values });
    setFilters(prev => {
      const newFilters = { ...prev, [column]: values };
      console.log('New filters:', newFilters);
      return newFilters;
    });
  };

  const filteredRisks = useMemo(() => {
    console.log('Filtering risks with filters:', filters);
    return detailedRisks.filter(risk => {
      console.log('Filtering risk:', risk);
      return Object.entries(filters).every(([key, selectedValues]) => {
        if (selectedValues.length === 0) return true;
        const riskValue = risk[key as keyof DetailedRiskReport]?.toString() || '';
        console.log(`Checking filter ${key}:`, { selectedValues, riskValue });
        return selectedValues.includes(riskValue);
      });
    });
  }, [detailedRisks, filters]);

  console.log('Filtered risks:', filteredRisks);

  if (!detailedRisks || detailedRisks.length === 0) {
    console.log('No detailed risks data available');
    return <div>Нет данных для детального отчета.</div>;
  }

  return (
    <div className="detailed-risks">
      <h3>Детальный отчет</h3>
      <div className="filters-container">
        {(Object.keys(filters) as Array<keyof FilterState>).map((column) => (
          <MultiSelectFilter
            key={column}
            column={column}
            values={uniqueValues[column]}
            selectedValues={filters[column]}
            onChange={handleFilterChange}
          />
        ))}
      </div>
      <div className="table-container">
        <table className="details-table">
          <thead>
            <tr>
              <th>Тип влияния</th>
              <th>Временные метрики</th>
              <th>Автопояснение по результату оценки рисков</th>
            </tr>
          </thead>
          <tbody>
            {filteredRisks.map((risk) => {
              console.log('Rendering risk:', risk);
              const rto = parseFloat(risk.rto_hours) || 0;
              const mtpd = parseFloat(risk.mtpd) || 0;
              const tr = parseFloat(risk.tr) || 0;

              const asReservedValue = risk.as_reserved_in_rcod ? risk.as_reserved_in_rcod.trim().toLowerCase() : '';

              let indicatorColor = '#ffc107'; // default yellow
              if (asReservedValue === 'да') {
                indicatorColor = '#28a745'; // green
              } else if (asReservedValue === 'нет') {
                indicatorColor = '#dc3545'; // red
              }

              return (
                <tr key={risk.id} style={{ position: 'relative', paddingBottom: '10px' }}>
                  <td style={{ position: 'relative', paddingLeft: '12px' }}>
                    <div style={{
                      position: 'absolute',
                      left: 0,
                      top: 0,
                      bottom: 0,
                      width: '6px',
                      backgroundColor: indicatorColor,
                      borderRadius: '2px'
                    }} />
                    {formatValue(risk.impact_type)}
                  </td>
                  <td className="metrics-cell">
                    {rto && mtpd && tr ? (
                      <TimeMetricsVisualizer
                        rto={rto}
                        mtpd={mtpd}
                        tr={tr}
                      />
                    ) : (
                      <span className="no-data">TR: {tr.toFixed(2)}ч, RTO: {rto.toFixed(2)}ч, MTPD: {mtpd.toFixed(2)}ч</span>
                    )}
                  </td>
                  <td>{formatValue(risk.risk_assessment_explanation)}</td>
                </tr>
              );
            })}
          </tbody>
          <tfoot>
            <tr>
              <td colSpan={3} style={{ paddingTop: '10px', fontStyle: 'italic' }}>
                <span style={{ backgroundColor: '#28a745', padding: '2px 6px', borderRadius: '3px', marginRight: '10px' }}></span> АС зарезервирована в РЦОД (да)<br />
                <span style={{ backgroundColor: '#dc3545', padding: '2px 6px', borderRadius: '3px', marginRight: '10px' }}></span> АС не зарезервирована в РЦОД (нет)<br />
                <span style={{ backgroundColor: '#ffc107', padding: '2px 6px', borderRadius: '3px', marginRight: '10px' }}></span> Статус резервирования неизвестен или не указан
              </td>
            </tr>
          </tfoot>
        </table>
      </div>
    </div>
  );
};

// Основное приложение
function App() {
  const [processes, setProcesses] = useState<Process[]>([]);
  const [filteredProcesses, setFilteredProcesses] = useState<Process[]>([]);
  const [selectedProcess, setSelectedProcess] = useState<Process | null>(null);
  const [threats, setThreats] = useState<Threat[]>([]);
  const [selectedThreat, setSelectedThreat] = useState<Threat | null>(null);
  const [selectedThreatDetails, setSelectedThreatDetails] = useState<RiskDetail | null>(null);

  const [detailedRisks, setDetailedRisks] = useState<DetailedRiskReport[]>([]);
  const [error, setError] = useState<string | null>(null);

  // Загрузка списка процессов при старте
  useEffect(() => {
    fetch('http://localhost:8000/processes')
      .then(response => response.json())
      .then(data => {
        setProcesses(data);
        setFilteredProcesses(data);
      })
      .catch(err => setError('Не удалось загрузить список процессов.'));
  }, []);

  // Функция поиска по процессам
  const handleProcessSearch = (query: string) => {
    const lowercaseQuery = query.toLowerCase();
    const filtered = processes.filter(process => 
      process.name.toLowerCase().includes(lowercaseQuery) ||
      process.sid.toLowerCase().includes(lowercaseQuery)
    );
    setFilteredProcesses(filtered);
  };

  // Загрузка угроз при выборе процесса
  useEffect(() => {
    if (selectedProcess) {
      fetch(`http://localhost:8000/threats/${selectedProcess.sid}`)
        .then(response => response.json())
        .then(data => {
          console.log('Received threats:', data);
          setThreats(data || []); // Убедимся, что threats всегда массив
          setSelectedThreat(null);
          setSelectedThreatDetails(null);
          setDetailedRisks([]);
        })
        .catch(err => {
          console.error('Error loading threats:', err);
          setError('Не удалось загрузить угрозы для процесса.');
          setThreats([]); // В случае ошибки устанавливаем пустой массив
        });
    }
  }, [selectedProcess]);

  // Загрузка деталей при выборе угрозы
  useEffect(() => {
    if (selectedThreat) {
      console.log('Loading details for threat:', selectedThreat);
      setError(null);
      
      // Загружаем детали риска
      fetch(`http://localhost:8000/risk-details/${selectedThreat.process_sid}`)
        .then(res => {
          if (!res.ok) throw new Error('Базовая информация не найдена.');
          return res.json();
        })
        .then(data => {
          console.log('Received risk details:', data);
          setSelectedThreatDetails(data);
        })
        .catch(err => {
          console.error('Error loading risk details:', err);
          setError(err.message);
          setSelectedThreatDetails(null);
        });

      // Загружаем детальный отчет
      fetch(`http://localhost:8000/detailed-risk-report/${selectedThreat.process_sid}`)
        .then(res => {
          if (!res.ok) throw new Error('Детальный отчет не найден.');
          return res.json();
        })
        .then(data => {
          console.log('Received detailed risks:', data);
          setDetailedRisks(Array.isArray(data) ? data : [data]); // Убедимся, что detailedRisks всегда массив
        })
        .catch(err => {
          console.error('Error loading detailed risks:', err);
          setError(err.message);
          setDetailedRisks([]);
        });
    }
  }, [selectedThreat]);

  // Debug: log detailedRisks data to verify data presence
  useEffect(() => {
    console.log('Detailed Risks data:', detailedRisks);
  }, [detailedRisks]);

  const getRiskColor = (level: string) => {
    const l = level.toLowerCase();
    if (l.includes('критический')) return '#dc3545';
    if (l.includes('высокий')) return '#ffc107';
    if (l.includes('средний')) return '#fd7e14';
    if (l.includes('низкий')) return '#28a745';
    return '#6c757d';
  };

  return (
    <div className="App">
      <div className="sidebar">
        <h2>Процессы</h2>
        <ProcessSearch onSearch={handleProcessSearch} />
        <div className="process-list">
          {filteredProcesses.map(p => (
            <div 
              key={p.id} 
              className={`process-item ${selectedProcess?.id === p.id ? 'selected' : ''}`}
              onClick={() => setSelectedProcess(p)}
            >
              <div className="process-name">{p.name}</div>
              <div className="process-sid">{p.sid}</div>
            </div>
          ))}
        </div>
      </div>
      <div className="main-content">
        {selectedProcess ? (
          <>
            <h2>Угрозы для: {selectedProcess.name}</h2>
            <div className="threats-container">
              {threats.map(threat => (
                <div
                  key={threat.id}
                  className={`threat-card ${selectedThreat?.id === threat.id ? 'selected' : ''}`}
                  onClick={() => setSelectedThreat(threat)}
                  style={{ 
                    borderLeft: `5px solid ${getRiskColor(threat.highest_risk_level)}`,
                    backgroundColor: getRiskColor(threat.integral_risk_level) + '20'
                  }}
                >
                  <div style={{ position: 'relative' }}>
                    <h3>{threat.type}</h3>
                    <div style={{
                      position: 'absolute',
                      top: 0,
                      right: 0,
                      padding: '4px 8px',
                      borderRadius: '4px',
                      backgroundColor: threat.threat_rating_color || getRiskColor(threat.threat_rating),
                      color: '#fff',
                      fontSize: '0.8em'
                    }}>
                      {threat.threat_rating || 'Нет данных'}
                    </div>
                  </div>
                  <p>{threat.scenario}</p>
                  <p><strong>Интегральный риск:</strong> {threat.integral_risk_level}</p>
                  <p><strong>Уровень наиболее высокого риска:</strong> {threat.highest_risk_level}</p>
                </div>
              ))}
            </div>
            <hr />
            {selectedThreat && (
              <div className="details-container">
                {error ? (
                  <div className="error-message">{error}</div>
                ) : (
                  <>
                    <RiskDetails riskDetails={selectedThreatDetails} />
                    <DetailedRisks detailedRisks={detailedRisks} />
                  </>
                )}
              </div>
            )}
          </>
        ) : (
          <div className="placeholder">Выберите процесс для просмотра информации.</div>
        )}
      </div>
    </div>
  );
}

export default App;
