import React, { useState, useEffect } from 'react';
import './index.css';

// Определяем интерфейсы для наших данных
interface Process {
  id: number;
  sid: string;
  name: string;
  risk_label: string;
  owner_block: string;
  department: string;
  rating: number;
}

interface Threat {
  id: number;
  type: string;
  scenario: string;
  integral_risk_level: string;
  highest_risk_level: string;
  process_sid: string;
  threat_rating: string;
  threat_rating_color: string;
}

interface RiskDetail {
  id: number;
  as_reserved_in_rcod: string;
  risk_label: string;
  rto_hours: string;
  mtpd: string;
  tr: string;
  high_risk_count?: number | string;
  total_risk_count?: number | string;
  process_threat_rating?: string;
}

interface DetailedRiskReport {
  id: number;
  process: string;
  process_sid: string;
  threat_type: string;
  threat_scenario: string;
  impact_type: string;
  risk_subcategory: string;
  risk_group: string;
  risk_subgroup: string;
  integral_risk: string;
  operational_risk: string;
  reputational_risk: string;
  regulatory_risk: string;
  financial_risk: string;
  impact_assessment: string;
  probability_assessment: string;
  control_assessment: string;
  risk_level: string;
  rto_hours: string;
  mtpd: string;
  tr: string;
  risk_assessment_explanation: string;
  as_reserved_in_rcod: string;
}

interface FilterState {
  impact_type: string[];
  rto_hours: string[];
  mtpd: string[];
  tr: string[];
  risk_assessment_explanation: string[];
}

// Функция для округления числовых значений
const roundMetricValue = (value: number): number => {
  if (value < 1) {
    // Для значений меньше 1 оставляем одну цифру после запятой
    return Math.round(value * 10) / 10;
  }
  // Для остальных значений округляем до целых
  return Math.round(value);
};

// Функция для форматирования значений
const formatValue = (value: string | number | null | undefined) => {
  console.log('Formatting value:', value);
  if (value === null || value === undefined || value === '') {
    console.log('Value is empty, returning "Нет данных"');
    return 'Нет данных';
  }
  if (typeof value === 'number') {
    return String(roundMetricValue(value));
  }
  console.log('Returning formatted value:', value);
  return String(value);
};

// Компонент поиска процессов
const ProcessSearch: React.FC<{ onSearch: (query: string) => void }> = ({ onSearch }) => {
  return (
    <div className="search-container">
      <input
        type="text"
        placeholder="Поиск по названию или коду процесса..."
        onChange={(e) => onSearch(e.target.value)}
        className="search-input"
      />
    </div>
  );
};

// Компонент визуализации временных метрик
const TimeMetricsVisualizer: React.FC<{
  rto: number;
  mtpd: number;
  tr: number;
}> = ({ rto, mtpd, tr }) => {
  return (
    <div className="time-metrics-container">
      <div className="metrics-timeline">
        <div className="timeline-labels">
          <div>0ч</div>
          <div>1ч</div>
          <div>2ч</div>
          <div>4ч</div>
          <div>8ч</div>
          <div>24ч</div>
        </div>
        <div className="timeline-grid">
          <div className="grid-line"></div>
          <div className="grid-line"></div>
          <div className="grid-line"></div>
          <div className="grid-line"></div>
          <div className="grid-line"></div>
          <div className="grid-line"></div>
        </div>
        <div className="timeline-metrics">
          <div className="metric rto" style={{ left: `${Math.min((rto / 24) * 100, 100)}%` }}>
            <div className="metric-line"></div>
            <div className="metric-label">RTO {rto < 1 ? rto.toFixed(1) : Math.round(rto)}ч</div>
          </div>
          <div className="metric mtpd" style={{ left: `${Math.min((mtpd / 24) * 100, 100)}%` }}>
            <div className="metric-line"></div>
            <div className="metric-label">MTPD {mtpd < 1 ? mtpd.toFixed(1) : Math.round(mtpd)}ч</div>
          </div>
          <div className="metric tr" style={{ left: `${Math.min((tr / 24) * 100, 100)}%` }}>
            <div className="metric-line"></div>
            <div className="metric-label">TR {tr < 1 ? tr.toFixed(1) : Math.round(tr)}ч</div>
          </div>
        </div>
      </div>
    </div>
  );
};

// Компонент мультиселект фильтра
const MultiSelectFilter: React.FC<{
  column: keyof FilterState;
  values: string[];
  selectedValues: string[];
  onChange: (column: keyof FilterState, values: string[]) => void;
}> = ({ column, values, selectedValues, onChange }) => {
  const [isOpen, setIsOpen] = useState(false);

  const getDisplayName = (column: string) => {
    const names: { [key: string]: string } = {
      impact_type: 'Тип влияния',
      rto_hours: 'RTO процесса',
      mtpd: 'MTPD процесса',
      tr: 'TR',
      risk_assessment_explanation: 'Автопояснение'
    };
    return names[column] || column;
  };

  const toggleValue = (value: string) => {
    const newValues = selectedValues.includes(value)
      ? selectedValues.filter(v => v !== value)
      : [...selectedValues, value];
    onChange(column, newValues);
  };

  return (
    <div className="multiselect-filter">
      <div 
        className="multiselect-header" 
        onClick={() => setIsOpen(!isOpen)}
      >
        <span>{getDisplayName(column)}</span>
        <span className="selected-count">
          {selectedValues.length ? `Выбрано: ${selectedValues.length}` : 'Все'}
        </span>
      </div>
      {isOpen && (
        <div className="multiselect-options">
          {values.map(value => (
            <label key={value} className="option-label">
              <input
                type="checkbox"
                checked={selectedValues.includes(value)}
                onChange={() => toggleValue(value)}
              />
              <span>{formatValue(value)}</span>
            </label>
          ))}
        </div>
      )}
    </div>
  );
};

// Компонент для отображения базовой информации о риске
const RiskDetails: React.FC<{ riskDetails: RiskDetail | null }> = ({ riskDetails }) => {
  console.log('RiskDetails component received:', riskDetails);

  if (!riskDetails) {
    console.log('No risk details data available');
    return <div>Нет данных для отображения.</div>;
  }

  return (
    <div className="risk-details">
      <h3>Базовая информация</h3>
      <div className="risk-info">
        <p><strong>Количество высоких рисков:</strong> {formatValue(riskDetails.high_risk_count)}</p>
        <p><strong>Общее количество рисков:</strong> {formatValue(riskDetails.total_risk_count)}</p>
        <p><strong>Рейтинг процесса для угрозы:</strong> {formatValue(riskDetails.process_threat_rating)}</p>
      </div>
    </div>
  );
};

// Компонент для отображения детального отчета
const DetailedRisks: React.FC<{ detailedRisks: DetailedRiskReport[] }> = ({ detailedRisks }) => {
  console.log('DetailedRisks component received:', detailedRisks);

  if (!detailedRisks || detailedRisks.length === 0) {
    return <div>Нет данных для отображения.</div>;
  }

  // Функция для определения цвета ячейки в зависимости от статуса резервирования
  const getReservationColor = (asReserved: string | undefined | null): string => {
    if (!asReserved) return '#6c757d'; // серый для неизвестного статуса
    const status = asReserved.toLowerCase();
    if (status === 'да') return '#28a745'; // зеленый для зарезервированных
    if (status === 'нет') return '#dc3545'; // красный для незарезервированных
    return '#6c757d'; // серый для всех остальных случаев
  };

  return (
    <div className="detailed-risks">
      <h3>Детальный отчет</h3>
      
      <div className="risks-table">
        <table>
          <thead>
            <tr>
              <th>Тип влияния</th>
              <th>Временные метрики</th>
              <th>Визуализация метрик</th>
              <th>Автопояснение</th>
            </tr>
          </thead>
          <tbody>
            {detailedRisks.map((risk, index) => {
              // Преобразуем строковые значения в числа для временных метрик
              const rtoHours = roundMetricValue(parseFloat(risk.rto_hours || '0'));
              const mtpdHours = roundMetricValue(parseFloat(risk.mtpd || '0'));
              const trHours = roundMetricValue(parseFloat(risk.tr || '0'));
              const hasValidTimeMetrics = !isNaN(rtoHours) && !isNaN(mtpdHours) && !isNaN(trHours);
              const reservationColor = getReservationColor(risk.as_reserved_in_rcod);

              return (
                <tr key={index}>
                  <td>{formatValue(risk.impact_type)}</td>
                  <td className="metrics-values">
                    <div className="metrics-container">
                      <div 
                        className="reservation-indicator"
                        style={{ backgroundColor: reservationColor }}
                      ></div>
                      <div className="metrics-content">
                        <div className="reservation-status">
                          АС {risk.as_reserved_in_rcod?.toLowerCase() === 'да' ? 'зарезервирована' : 'не зарезервирована'} в РЦОД
                        </div>
                        <div className="metrics-list">
                          <p><strong>TR:</strong> {formatValue(trHours)}</p>
                          <p><strong>RTO:</strong> {formatValue(rtoHours)}</p>
                          <p><strong>MTPD:</strong> {formatValue(mtpdHours)}</p>
                        </div>
                      </div>
                    </div>
                  </td>
                  <td className="metrics-cell">
                    {hasValidTimeMetrics && (
                      <TimeMetricsVisualizer
                        rto={rtoHours}
                        mtpd={mtpdHours}
                        tr={trHours}
                      />
                    )}
                  </td>
                  <td>{formatValue(risk.risk_assessment_explanation)}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
};

// Основное приложение
function App() {
  const [processes, setProcesses] = useState<Process[]>([]);
  const [filteredProcesses, setFilteredProcesses] = useState<Process[]>([]);
  const [selectedProcess, setSelectedProcess] = useState<Process | null>(null);
  const [threats, setThreats] = useState<Threat[]>([]);
  const [selectedThreat, setSelectedThreat] = useState<Threat | null>(null);
  const [selectedThreatDetails, setSelectedThreatDetails] = useState<RiskDetail | null>(null);

  const [detailedRisks, setDetailedRisks] = useState<DetailedRiskReport[]>([]);
  const [error, setError] = useState<string | null>(null);

  // Загрузка списка процессов при старте
  useEffect(() => {
    fetch('http://localhost:8000/processes')
      .then(response => response.json())
      .then(data => {
        setProcesses(data);
        setFilteredProcesses(data);
      })
      .catch(err => setError('Не удалось загрузить список процессов.'));
  }, []);

  // Функция поиска по процессам
  const handleProcessSearch = (query: string) => {
    const lowercaseQuery = query.toLowerCase();
    const filtered = processes.filter(process => 
      process.name.toLowerCase().includes(lowercaseQuery) ||
      process.sid.toLowerCase().includes(lowercaseQuery)
    );
    setFilteredProcesses(filtered);
  };

  // Загрузка угроз при выборе процесса
  useEffect(() => {
    // Очищаем состояние при смене процесса
    setThreats([]);
    setSelectedThreat(null);
    setSelectedThreatDetails(null);
    setDetailedRisks([]);
    setError(null);

    if (selectedProcess) {
      fetch(`http://localhost:8000/threats/${selectedProcess.sid}`)
        .then(response => response.json())
        .then(data => {
          console.log('Received threats:', data);
          setThreats(data || []); // Убедимся, что threats всегда массив
        })
        .catch(err => {
          console.error('Error loading threats:', err);
          setError('Не удалось загрузить угрозы для процесса.');
        });
    }
  }, [selectedProcess]);

  // Загрузка деталей при выборе угрозы
  useEffect(() => {
    if (selectedThreat) {
      console.log('Loading details for threat:', selectedThreat);
      setError(null);
      
      // Загружаем детали риска
      fetch(`http://localhost:8000/risk-details/${selectedThreat.process_sid}?threat_type=${encodeURIComponent(selectedThreat.type)}&threat_scenario=${encodeURIComponent(selectedThreat.scenario)}`)
        .then(res => {
          if (!res.ok) throw new Error('Базовая информация не найдена.');
          return res.json();
        })
        .then(data => {
          console.log('Received risk details:', data);
          setSelectedThreatDetails(data);
        })
        .catch(err => {
          console.error('Error loading risk details:', err);
          setError(err.message);
          setSelectedThreatDetails(null);
        });

      // Загружаем детальный отчет
      fetch(`http://localhost:8000/detailed-risk-report/${selectedThreat.process_sid}?threat_type=${encodeURIComponent(selectedThreat.type)}&threat_scenario=${encodeURIComponent(selectedThreat.scenario)}`)
        .then(res => {
          if (!res.ok) throw new Error('Детальный отчет не найден.');
          return res.json();
        })
        .then(data => {
          console.log('Received detailed risks:', data);
          setDetailedRisks(Array.isArray(data) ? data : [data]); // Убедимся, что detailedRisks всегда массив
        })
        .catch(err => {
          console.error('Error loading detailed risks:', err);
          setError(err.message);
          setDetailedRisks([]);
        });
    }
  }, [selectedThreat]);

  // Debug: log detailedRisks data to verify data presence
  useEffect(() => {
    console.log('Detailed Risks data:', detailedRisks);
  }, [detailedRisks]);

  const getRiskColor = (level: string | undefined | null): string => {
    if (!level) return '#6c757d'; // Возвращаем серый цвет для undefined/null значений
    
    const l = level.toLowerCase();
    if (l.includes('критический')) return '#dc3545';
    if (l.includes('высокий')) return '#ffc107';
    if (l.includes('средний')) return '#fd7e14';
    if (l.includes('низкий')) return '#28a745';
    return '#6c757d';
  };

  return (
    <div className="App">
      <div className="sidebar">
        <h2>Процессы</h2>
        <ProcessSearch onSearch={handleProcessSearch} />
        <div className="process-list">
          {filteredProcesses.map(p => (
            <div 
              key={p.sid} 
              className={`process-item ${selectedProcess?.sid === p.sid ? 'selected' : ''}`}
              onClick={() => setSelectedProcess(p)}
            >
              <div className="process-name">{p.name}</div>
              <div className="process-sid">{p.sid}</div>
            </div>
          ))}
        </div>
      </div>
      <div className="main-content">
        {selectedProcess ? (
          <>
            <h2>Угрозы для: {selectedProcess.name}</h2>
            <div className="threats-container">
              {threats.map(threat => (
                <div
                  key={`${threat.process_sid}-${threat.type}-${threat.scenario}`}
                  className={`threat-card ${
                    selectedThreat?.process_sid === threat.process_sid && 
                    selectedThreat?.type === threat.type && 
                    selectedThreat?.scenario === threat.scenario 
                    ? 'selected' : ''
                  }`}
                  onClick={() => setSelectedThreat(threat)}
                  style={{ 
                    borderLeft: `5px solid ${getRiskColor(threat.highest_risk_level)}`,
                    backgroundColor: getRiskColor(threat.integral_risk_level) + '20'
                  }}
                >
                  <div style={{ position: 'relative' }}>
                    <h3>{threat.type}</h3>
                    <div style={{
                      position: 'absolute',
                      top: 0,
                      right: 0,
                      padding: '4px 8px',
                      borderRadius: '4px',
                      backgroundColor: threat.threat_rating_color || getRiskColor(threat.threat_rating),
                      color: '#fff',
                      fontSize: '0.8em'
                    }}>
                      {threat.threat_rating || 'Нет данных'}
                    </div>
                  </div>
                  <p>{threat.scenario}</p>
                  <p><strong>Интегральный риск:</strong> {formatValue(threat.integral_risk_level)}</p>
                  <p><strong>Уровень наиболее высокого риска:</strong> {formatValue(threat.highest_risk_level)}</p>
                </div>
              ))}
            </div>
            <hr />
            {selectedThreat && (
              <div className="details-container">
                {error ? (
                  <div className="error-message">{error}</div>
                ) : (
                  <>
                    <RiskDetails riskDetails={selectedThreatDetails} />
                    <DetailedRisks detailedRisks={detailedRisks} />
                  </>
                )}
              </div>
            )}
          </>
        ) : (
          <div className="placeholder">Выберите процесс для просмотра информации.</div>
        )}
      </div>
    </div>
  );
}

export default App;
