import React, { useState, useEffect } from 'react';
import {
  Container,
  TextField,
  List,
  ListItem,
  ListItemText,
  Typography,
  Paper,
  Box,
  Chip,
  Grid,
  Card,
  CardContent,
  Modal,
  IconButton,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Tabs,
  Tab,
  Button
} from '@mui/material';
import CloseIcon from '@mui/icons-material/Close';

interface Process {
  id: number;
  sid: string;
  name: string;
  risk_label: string;
  owner_block: string;
  department: string;
  rating: number;
}

interface Threat {
  id: number;
  type: string;
  scenario: string;
  integral_risk_level: string;
  highest_risk_level: string;
}

interface RiskDetail {
  id: number;
  process_sid: string;
  threat_type: string;
  threat_scenario: string;
  impact_type: string;
  risk_impact: string;
  risk_assessment: string;
  risk_label: string;
  risk_assessment_explanation: string;
  as_reserved_in_rcod: string;
  rto_hours: string;
  mtpd: string;
  tr: string;
}

interface DetailedRiskReport {
  id: number;
  process: string;
  threat_type: string;
  threat_scenario: string;
  impact_type: string;
  risk_subcategory: string;
  risk_group: string;
  risk_subgroup: string;
  integral_risk: string;
  operational_risk: string;
  reputational_risk: string;
  regulatory_risk: string;
  financial_risk: string;
  impact_assessment: string;
  probability_assessment: string;
  control_assessment: string;
  risk_level: string;
  rto_hours: string;
  mtpd: string;
  tr: string;
  risk_assessment_explanation: string;
}

const styles = `
  .App {
    display: flex;
    height: 100vh;
  }

  .sidebar {
    width: 300px;
    padding: 20px;
    background-color: #f5f5f5;
    overflow-y: auto;
  }

  .main-content {
    flex: 1;
    padding: 20px;
    overflow-y: auto;
  }

  .threats-container {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
    gap: 20px;
    margin-bottom: 20px;
  }

  .threat-card {
    padding: 15px;
    border: 2px solid;
    border-radius: 8px;
    cursor: pointer;
    transition: all 0.3s ease;
  }

  .threat-card:hover {
    transform: translateY(-2px);
    box-shadow: 0 4px 8px rgba(0,0,0,0.1);
  }

  .threat-card.selected {
    box-shadow: 0 0 0 2px #000;
  }

  .details-container {
    margin-top: 20px;
  }

  .details-table {
    width: 100%;
    border-collapse: collapse;
    margin-top: 10px;
    margin-bottom: 20px;
  }

  .details-table th,
  .details-table td {
    padding: 12px;
    border: 1px solid #ddd;
    text-align: left;
  }

  .details-table th {
    background-color: #f8f9fa;
    font-weight: bold;
  }

  .details-table tr:nth-child(even) {
    background-color: #f8f9fa;
  }

  .details-table tr:hover {
    background-color: #f2f2f2;
  }

  .risk-details h3,
  .detailed-risks h3 {
    margin-top: 20px;
    margin-bottom: 10px;
    font-size: 1.2em;
  }
`;

function App() {
  const [processes, setProcesses] = useState<Process[]>([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedProcess, setSelectedProcess] = useState<Process | null>(null);
  const [threats, setThreats] = useState<Threat[]>([]);
  const [selectedThreat, setSelectedThreat] = useState<Threat | null>(null);
  const [riskDetails, setRiskDetails] = useState<RiskDetail[]>([]);
  const [detailedRisks, setDetailedRisks] = useState<DetailedRiskReport[]>([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [activeTab, setActiveTab] = useState(0);

  useEffect(() => {
    if (searchTerm) {
      fetch(`http://localhost:8000/search?q=${encodeURIComponent(searchTerm)}`)
        .then(response => response.json())
        .then(data => setProcesses(data));
    } else {
      fetch('http://localhost:8000/processes')
        .then(response => response.json())
        .then(data => setProcesses(data));
    }
  }, [searchTerm]);

  useEffect(() => {
    if (selectedProcess) {
      fetch(`http://localhost:8000/process/${selectedProcess.id}/threats`)
        .then(response => response.json())
        .then(data => setThreats(data));
    }
  }, [selectedProcess]);

  useEffect(() => {
    if (selectedThreat) {
      console.log('Загрузка деталей для угрозы:', selectedThreat);
      // Загружаем базовые детали риска
      fetch(`http://localhost:8000/threat/${selectedThreat.id}/risk-details`)
        .then(response => {
          if (!response.ok) {
            throw new Error('Risk details not found');
          }
          return response.json();
        })
        .then(data => {
          console.log('Получены базовые детали:', data);
          setRiskDetails(data || []);
        })
        .catch(error => {
          console.error('Error fetching risk details:', error);
          setRiskDetails([]);
        });

      // Загружаем расширенные детали риска
      fetch(`http://localhost:8000/threat/${selectedThreat.id}/detailed-risks`)
        .then(response => {
          if (!response.ok) {
            throw new Error('Detailed risks not found');
          }
          return response.json();
        })
        .then(data => {
          console.log('Получены детальные риски:', data);
          setDetailedRisks(data || []);
        })
        .catch(error => {
          console.error('Error fetching detailed risks:', error);
          setDetailedRisks([]);
        });
    }
  }, [selectedThreat]);

  const handleThreatClick = (threat: Threat) => {
    console.log('Клик по угрозе:', threat);
    setSelectedThreat(threat);
    setIsModalOpen(true); // Добавляем явное открытие модального окна
  };

  const handleCloseModal = () => {
    console.log('Закрытие модального окна');
    setIsModalOpen(false);
    setSelectedThreat(null);
    setActiveTab(0);
  };

  const handleTabChange = (event: React.SyntheticEvent, newValue: number) => {
    setActiveTab(newValue);
  };

  const getRiskLevelColor = (level: string) => {
    const levelLower = level.toLowerCase();
    if (levelLower.includes('высок')) return '#ff4444';
    if (levelLower.includes('средн')) return '#ffbb33';
    if (levelLower.includes('низк')) return '#00C851';
    return '#33b5e5';
  };

  const RiskDetails: React.FC<{ riskDetails: RiskDetail | null }> = ({ riskDetails }) => {
    if (!riskDetails) {
      return <div>Нет доступных деталей риска</div>;
    }

    const formatValue = (value: string | null | undefined) => {
      if (!value || value === 'nan' || value === '') return 'Нет данных';
      return value;
    };

    return (
      <div className="risk-details">
        <h3>Базовая информация</h3>
        <table className="details-table">
          <tbody>
            <tr>
              <td>АС зарезервирована в РЦОД:</td>
              <td>{formatValue(riskDetails.as_reserved_in_rcod)}</td>
            </tr>
            <tr>
              <td>Метка риска:</td>
              <td>{formatValue(riskDetails.risk_label)}</td>
            </tr>
            <tr>
              <td>RTO процесса, ч.:</td>
              <td>{formatValue(riskDetails.rto_hours)}</td>
            </tr>
            <tr>
              <td>MTPD процесса:</td>
              <td>{formatValue(riskDetails.mtpd)}</td>
            </tr>
            <tr>
              <td>TR:</td>
              <td>{formatValue(riskDetails.tr)}</td>
            </tr>
          </tbody>
        </table>
      </div>
    );
  };

  const DetailedRisks: React.FC<{ detailedRisks: DetailedRiskReport[] }> = ({ detailedRisks }) => {
    if (!detailedRisks.length) {
      return <div>Нет доступных детальных рисков</div>;
    }

    const formatValue = (value: string | null | undefined) => {
      if (!value || value === 'nan' || value === '') return 'Нет данных';
      return value;
    };

    return (
      <div className="detailed-risks">
        <h3>Детальный отчет</h3>
        <table className="details-table">
          <thead>
            <tr>
              <th>Тип влияния</th>
              <th>RTO процесса, ч.</th>
              <th>MTPD процесса</th>
              <th>TR</th>
              <th>Автопояснение по результату оценки рисков</th>
            </tr>
          </thead>
          <tbody>
            {detailedRisks.map((risk, index) => (
              <tr key={index}>
                <td>{formatValue(risk.impact_type)}</td>
                <td>{formatValue(risk.rto_hours)}</td>
                <td>{formatValue(risk.mtpd)}</td>
                <td>{formatValue(risk.tr)}</td>
                <td>{formatValue(risk.risk_assessment_explanation)}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4 }}>
      <Typography variant="h4" gutterBottom>
        Анализ рисков процессов
      </Typography>
      
      <TextField
        fullWidth
        label="Поиск по процессам"
        variant="outlined"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        sx={{ mb: 4 }}
      />

      <Grid container spacing={3}>
        <Grid item xs={12} md={4}>
          <Paper sx={{ maxHeight: 'calc(100vh - 200px)', overflow: 'auto' }}>
            <List>
              {processes.map((process) => (
                <ListItem
                  key={process.id}
                  button
                  selected={selectedProcess?.id === process.id}
                  onClick={() => setSelectedProcess(process)}
                >
                  <ListItemText
                    primary={process.name}
                    secondary={`SID: ${process.sid}`}
                  />
                </ListItem>
              ))}
            </List>
          </Paper>
        </Grid>

        <Grid item xs={12} md={8}>
          {selectedProcess && (
            <Card>
              <CardContent>
                <Typography variant="h5" gutterBottom>
                  {selectedProcess.name}
                </Typography>
                
                <Box sx={{ mb: 2 }}>
                  <Typography variant="subtitle1" color="text.secondary">
                    SID: {selectedProcess.sid}
                  </Typography>
                  <Typography variant="body2">
                    Блок-владелец: {selectedProcess.owner_block}
                  </Typography>
                  <Typography variant="body2">
                    Подразделение: {selectedProcess.department}
                  </Typography>
                </Box>

                <Typography variant="h6" gutterBottom sx={{ mt: 3 }}>
                  Угрозы процесса:
                </Typography>
                
                {threats.map((threat) => (
                  <Button
                    key={threat.id}
                    onClick={() => handleThreatClick(threat)}
                    fullWidth
                    sx={{
                      mb: 2,
                      textAlign: 'left',
                      display: 'block'
                    }}
                  >
                    <Paper 
                      sx={{ 
                        p: 2,
                        width: '100%'
                      }}
                      elevation={2}
                    >
                      <Typography variant="subtitle1">
                        Тип угрозы: {threat.type}
                      </Typography>
                      <Typography variant="body2" sx={{ mb: 1 }}>
                        Сценарий: {threat.scenario}
                      </Typography>
                      <Box sx={{ mt: 2 }}>
                        <Chip
                          label={`Интегральный уровень риска: ${threat.integral_risk_level}`}
                          sx={{
                            bgcolor: getRiskLevelColor(threat.integral_risk_level),
                            color: 'white',
                            mb: 1,
                            mr: 1
                          }}
                        />
                        <Chip
                          label={`Наивысший уровень риска: ${threat.highest_risk_level}`}
                          sx={{
                            bgcolor: getRiskLevelColor(threat.highest_risk_level),
                            color: 'white'
                          }}
                        />
                      </Box>
                    </Paper>
                  </Button>
                ))}
              </CardContent>
            </Card>
          )}
        </Grid>
      </Grid>

      <Modal
        open={isModalOpen}
        onClose={handleCloseModal}
        aria-labelledby="modal-title"
        aria-describedby="modal-description"
      >
        <Box sx={{
          position: 'absolute',
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          width: '90%',
          maxWidth: 1000,
          bgcolor: 'background.paper',
          boxShadow: 24,
          p: 4,
          maxHeight: '90vh',
          overflow: 'auto'
        }}>
          {selectedThreat && (
            <>
              <IconButton
                aria-label="close"
                onClick={handleCloseModal}
                sx={{
                  position: 'absolute',
                  right: 8,
                  top: 8
                }}
              >
                <CloseIcon />
              </IconButton>

              <Typography variant="h6" gutterBottom>
                {selectedThreat.type}
              </Typography>
              <Typography variant="body1" gutterBottom>
                {selectedThreat.scenario}
              </Typography>

              <Box sx={{ borderBottom: 1, borderColor: 'divider', mb: 2 }}>
                <Tabs value={activeTab} onChange={handleTabChange}>
                  <Tab label="Базовая информация" />
                  <Tab label="Детальный отчет" />
                </Tabs>
              </Box>

              {activeTab === 0 && (
                <>
                  <Typography variant="h6" gutterBottom>
                    Детали риска
                  </Typography>
                  {riskDetails.length > 0 ? (
                    <div>
                      <Typography variant="h6" gutterBottom>
                        Детали риска
                      </Typography>
                      {riskDetails.map((detail) => (
                        <Box key={detail.id} sx={{ mb: 2 }}>
                          <Typography variant="body1">
                            <strong>Метка риска:</strong> {detail.risk_label}
                          </Typography>
                          <Typography variant="body1">
                            <strong>АС зарезервирована в РЦОД:</strong> {detail.as_reserved_in_rcod}
                          </Typography>
                          <Typography variant="body1">
                            <strong>RTO процесса:</strong> {detail.rto_hours} ч.
                          </Typography>
                          <Typography variant="body1">
                            <strong>MTPD процесса:</strong> {detail.mtpd}
                          </Typography>
                          <Typography variant="body1">
                            <strong>TR:</strong> {detail.tr}
                          </Typography>
                        </Box>
                      ))}
                    </div>
                  ) : (
                    <Typography>Нет доступных деталей риска</Typography>
                  )}
                </>
              )}

              {activeTab === 1 && (
                <>
                  <Typography variant="h6" gutterBottom>
                    Детальный отчет по процессу
                  </Typography>
                  <Box sx={{ mb: 3 }}>
                    <Typography variant="subtitle1" color="text.secondary">
                      Процесс: {selectedProcess?.name}
                    </Typography>
                    <Typography variant="body2">
                      SID: {selectedProcess?.sid}
                    </Typography>
                    <Typography variant="body2">
                      Блок-владелец: {selectedProcess?.owner_block}
                    </Typography>
                  </Box>
                  {Array.isArray(detailedRisks) && detailedRisks.length > 0 ? (
                    <TableContainer component={Paper}>
                      <Table>
                        <TableHead>
                          <TableRow>
                            <TableCell>Тип влияния</TableCell>
                            <TableCell>RTO процесса, ч.</TableCell>
                            <TableCell>MTPD процесса</TableCell>
                            <TableCell>TR</TableCell>
                            <TableCell>Автопояснение</TableCell>
                          </TableRow>
                        </TableHead>
                        <TableBody>
                          {detailedRisks.map((risk) => (
                            <TableRow key={risk.id}>
                              <TableCell>{risk.impact_type}</TableCell>
                              <TableCell>{risk.rto_hours}</TableCell>
                              <TableCell>{risk.mtpd}</TableCell>
                              <TableCell>{risk.tr}</TableCell>
                              <TableCell>{risk.risk_assessment_explanation}</TableCell>
                            </TableRow>
                          ))}
                        </TableBody>
                      </Table>
                    </TableContainer>
                  ) : (
                    <Typography>Нет доступных детальных данных для этого процесса</Typography>
                  )}
                </>
              )}
            </>
          )}
        </Box>
      </Modal>
    </Container>
  );
}

export default App;
