import React, { useState, useEffect } from 'react';
import './App.css';

// Определяем интерфейсы для наших данных
interface Process {
  id: number;
  sid: string;
  name: string;
  risk_label: string;
  owner_block: string;
  department: string;
  rating: number;
}

interface Threat {
  id: number;
  type: string;
  scenario: string;
  integral_risk_level: string;
  highest_risk_level: string;
  process_sid: string;
}

interface RiskDetail {
  id: number;
  as_reserved_in_rcod: string;
  risk_label: string;
  rto_hours: string;
  mtpd: string;
  tr: string;
}

interface DetailedRiskReport {
  id: number;
  impact_type: string;
  rto_hours: string;
  mtpd: string;
  tr: string;
  risk_assessment_explanation: string;
}

// --- Компоненты для отображения ---

// Функция для форматирования значений (пусто, 'nan' -> 'Нет данных')
const formatValue = (value: string | null | undefined) => {
  if (!value || value.trim().toLowerCase() === 'nan' || value.trim() === '') {
    return 'Нет данных';
  }
  return value;
};

// Компонент для отображения базовой информации о риске
const RiskDetails: React.FC<{ riskDetails: RiskDetail | null }> = ({ riskDetails }) => {
  if (!riskDetails) {
    return <div>Нет данных для отображения.</div>;
  }
  return (
    <div className="risk-details">
      <h3>Базовая информация</h3>
      <table className="details-table">
        <tbody>
          <tr>
            <td>АС зарезервирована в РЦОД:</td>
            <td>{formatValue(riskDetails.as_reserved_in_rcod)}</td>
          </tr>
          <tr>
            <td>Метка риска:</td>
            <td>{formatValue(riskDetails.risk_label)}</td>
          </tr>
          <tr>
            <td>RTO процесса, ч.:</td>
            <td>{formatValue(riskDetails.rto_hours)}</td>
          </tr>
          <tr>
            <td>MTPD процесса:</td>
            <td>{formatValue(riskDetails.mtpd)}</td>
          </tr>
          <tr>
            <td>TR:</td>
            <td>{formatValue(riskDetails.tr)}</td>
          </tr>
        </tbody>
      </table>
    </div>
  );
};

// Компонент для детального отчета
const DetailedRisks: React.FC<{ detailedRisks: DetailedRiskReport[] }> = ({ detailedRisks }) => {
  if (!detailedRisks || detailedRisks.length === 0) {
    return <div>Нет данных для детального отчета.</div>;
  }
  return (
    <div className="detailed-risks">
      <h3>Детальный отчет</h3>
      <table className="details-table">
        <thead>
          <tr>
            <th>Тип влияния</th>
            <th>RTO процесса, ч.</th>
            <th>MTPD процесса</th>
            <th>TR</th>
            <th>Автопояснение по результату оценки рисков</th>
          </tr>
        </thead>
        <tbody>
          {detailedRisks.map((risk) => (
            <tr key={risk.id}>
              <td>{formatValue(risk.impact_type)}</td>
              <td>{formatValue(risk.rto_hours)}</td>
              <td>{formatValue(risk.mtpd)}</td>
              <td>{formatValue(risk.tr)}</td>
              <td>{formatValue(risk.risk_assessment_explanation)}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

// --- Основное приложение ---

function App() {
  const [processes, setProcesses] = useState<Process[]>([]);
  const [selectedProcess, setSelectedProcess] = useState<Process | null>(null);
  const [threats, setThreats] = useState<Threat[]>([]);
  const [selectedThreat, setSelectedThreat] = useState<Threat | null>(null);
  const [selectedThreatDetails, setSelectedThreatDetails] = useState<RiskDetail | null>(null);
  const [detailedRisks, setDetailedRisks] = useState<DetailedRiskReport[]>([]);
  const [error, setError] = useState<string | null>(null);

  // Загрузка списка процессов при старте
  useEffect(() => {
    fetch('http://localhost:8000/processes')
      .then(response => response.json())
      .then(data => setProcesses(data))
      .catch(err => setError('Не удалось загрузить список процессов.'));
  }, []);

  // Загрузка угроз при выборе процесса
  useEffect(() => {
    if (selectedProcess) {
      fetch(`http://localhost:8000/process/${selectedProcess.id}/threats`)
        .then(response => response.json())
        .then(data => {
          setThreats(data);
          setSelectedThreat(null); // Сбрасываем выбор угрозы
          setSelectedThreatDetails(null);
          setDetailedRisks([]);
        })
        .catch(err => setError('Не удалось загрузить угрозы для процесса.'));
    }
  }, [selectedProcess]);

  // Загрузка деталей при выборе угрозы
  useEffect(() => {
    if (selectedThreat) {
      setError(null);
      const threatId = selectedThreat.id;
      
      const fetchRiskDetails = fetch(`http://localhost:8000/threat/${threatId}/risk-details`)
        .then(res => {
          if (!res.ok) throw new Error('Базовая информация не найдена.');
          return res.json();
        });
        
      const fetchDetailedReport = fetch(`http://localhost:8000/threat/${threatId}/detailed-risks`)
        .then(res => {
          if (!res.ok) throw new Error('Детальный отчет не найден.');
          return res.json();
        });

      Promise.all([fetchRiskDetails, fetchDetailedReport])
        .then(([riskDetailsData, detailedReportData]) => {
          setSelectedThreatDetails(riskDetailsData);
          setDetailedRisks(detailedReportData);
        })
        .catch(err => {
          setError(err.message);
          setSelectedThreatDetails(null);
          setDetailedRisks([]);
        });
    }
  }, [selectedThreat]);

  const getRiskColor = (level: string) => {
    const l = level.toLowerCase();
    if (l.includes('критический')) return '#dc3545';
    if (l.includes('высокий')) return '#ffc107';
    if (l.includes('средний')) return '#fd7e14';
    if (l.includes('низкий')) return '#28a745';
    return '#6c757d';
  };

  return (
    <div className="App">
      <div className="sidebar">
        <h2>Процессы</h2>
        {processes.map(p => (
          <div 
            key={p.id} 
            className={`process-item ${selectedProcess?.id === p.id ? 'selected' : ''}`}
            onClick={() => setSelectedProcess(p)}
          >
            {p.name}
          </div>
        ))}
      </div>
      <div className="main-content">
        {selectedProcess ? (
          <>
            <h2>Угрозы для: {selectedProcess.name}</h2>
            <div className="threats-container">
              {threats.map(threat => (
                <div
                  key={threat.id}
                  className={`threat-card ${selectedThreat?.id === threat.id ? 'selected' : ''}`}
                  onClick={() => setSelectedThreat(threat)}
                  style={{ borderLeft: `5px solid ${getRiskColor(threat.highest_risk_level)}` }}
                >
                  <h3>{threat.type}</h3>
                  <p>{threat.scenario}</p>
                  <p><strong>Интегральный риск:</strong> {threat.integral_risk_level}</p>
                </div>
              ))}
            </div>
            <hr />
            {selectedThreat && (
              <div className="details-container">
                {error ? (
                  <div className="error-message">{error}</div>
                ) : (
                  <>
                    <RiskDetails riskDetails={selectedThreatDetails} />
                    <DetailedRisks detailedRisks={detailedRisks} />
                  </>
                )}
              </div>
            )}
          </>
        ) : (
          <div className="placeholder">Выберите процесс для просмотра информации.</div>
        )}
      </div>
    </div>
  );
}

export default App;
